INSERT INTO Recipes (recipe_id, recipe_name, ingredients, calories, protein, fat, carbohydrates, sodium, main_image_url, category, method, serving_weight, hashtag, recipe_tip) VALUES
(28, '새우 두부 계란찜', '새우두부계란찜 연두부 75g(3/4모), 칵테일새우 20g(5마리), 달걀 30g(1/2개), 생크림 13g(1큰술), 설탕 5g(1작은술), 무염버터 5g(1작은술) 고명 시금치 10g(3줄기)', 220.00, 14.00, 17.00, 3.00, 99.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00028_2.png', '반찬', '찌기', NULL, '연두부', '나트륨의 배출을 도와주는 것으로 알려진 칼륨이 풍부한 시금치와 소금, 간장 등의 양념 대신 새우에 들어있는 간으로 맛을 내요.'),
(29, '부추 콩가루 찜', '[1인분]조선부추 50g, 날콩가루 7g(1⅓작은술) ·양념장 : 저염간장 3g(2/3작은술), 다진 대파 5g(1작은술), 다진 마늘 2g(1/2쪽), 고춧가루 2g(1/3작은술), 요리당 2g(1/3작은술), 참기름 2g(1/3작은술), 참깨 약간', 215.00, 14.00, 9.00, 20.00, 240.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00029_2.png', '반찬', '찌기', NULL, '날콩가루', '콩가루로 버무려 감칠맛과 고소한 맛으로 나트륨 사용량을 줄여보세요. 부추는 피를 맑게 하고 허약 체질을 개선하여 성인병을 예방하는데 효과가 있지만, 알레르기 체질이나 위장이 약한 사람은 부작용이 생길 수 있으니 주의하세요.'),
(31, '방울토마토 소박이', '●방울토마토 소박이 : 방울토마토 150g(5개), 양파 10g(3×1cm), 부추 10g(5줄기) ●양념장 : 고춧가루 4g(1작은술), 멸치액젓 3g(2/3작은술), 다진 마늘 2.5g(1/2쪽), 매실액 2g(1/3작은술), 설탕 2g(1/3작은술), 물 2ml(1/3작은술), 통깨 약간', 45.00, 2.00, 1.00, 9.00, 277.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00031_2.png', '반찬', '기타', NULL, '방울토마토', '소금에 절이는 오이 대신 방울토마토를 사용하여 나트륨 섭취를 줄였어요. 토마토에는 과일에 대체로 없는 글루탐산이 풍부하여 감칠맛을 내주며, 겉절이 양념과 잘 어우러져 상큼함과 감칠맛을 내주어요.'),
(32, '순두부 사과 소스 오이무침', '●오이무침 : 오이 70g(1/3개), 다진 땅콩 10g(1큰술) ●순두부사과 소스 : 순두부 40g(1/8봉지), 사과 50g(1/3개)', 75.00, 4.00, 2.00, 10.00, 22.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00032_2.png', '반찬', '기타', NULL, '순두부', '사과에는 팩틴이 풍부하여 체내 나쁜 콜레스테롤을 감소시키고 나트륨 배출을 촉진시켜줘요. 순두부와 사과를 갈아 만든 소스는 맵고 짠 자극적인 간이 아닌 재료 그대로의 새콤달콤한 맛과 깔끔한 맛을 내요.'),
(33, '사과 새우 북엇국', '북엇국 북어채 25g(15개), 새우 10g(3마리), 사과 30g(1/5개), 양파 40g(1/4개), 표고버섯 20g(2장), 물 300ml(1½컵)', 65.00, 12.00, 1.00, 2.00, 78.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00033_2.png', '국&찌개', '끓이기', NULL, NULL, '소금과 간장 대신 북어채와 새우의 짠맛으로 간을 한 담백한 맛의 북엇국을 만들었어요. 홍합이나 바지락을 넣으면 시원한 국물을 연출 할 수 있어요.'),
(36, '저염 된장으로 맛을 낸 황태해장국', '황태해장국 황태(채) 15g(10개), 콩나물 30g(1/6봉지), 무 30g(5×3×2cm), 저염된장 10g(2작은술), 물 300ml(1½컵), 청양고추 5g(1/2개), 다진 마늘 2g(1/3작은술)', 100.00, 13.00, 0.00, 12.00, 361.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00036_2.png', '국&찌개', '끓이기', NULL, '저염된장', '황태해장국을 조리할 때 소금 대신 저염된장을 사용하면 덜 짜고 감칠맛 나는 황태해장국을 맛 볼 수 있어요. 황태는 빛이 누렇고 살이 연한 것이 좋은 상품이며, 황태는 냉동실에 보관하거나 잘 밀봉하여 건조하고 서늘한 곳에 보관하는 것이 좋아요.'),
(37, '된장국', '된장국 두부 20g(2×2×2cm), 애느타리버섯 20g(4가닥), 감자 10g(4×3×1cm), 양파 10g(2×1cm), 대파 10g(5cm), 된장 5g(1작은술), 물 300ml(1½컵)', 20.00, 2.00, 0.00, 3.00, 260.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00037_2.png', '국&찌개', '끓이기', NULL, NULL, '된장국의 된장 사용량을 줄이기 위해 두부와 감자 등의 재료를 갈아 넣으면 담백한 된장국을 맛볼 수 있어요. 된장의 양을 적게 사용함으로 나트륨 섭취를 줄일 수 있어요.'),
(38, '표고버섯 청경채국', '●멸치육수 : 국물용 멸치 5g(3마리), 다시마 1장(5×1cm), 양파 10g(2×1cm), 표고버섯 기둥, 국간장 5g(1작은술), 물 300ml(1½컵) ●채소준비 : 청경채 20g(1개), 표고버섯 20g(2장), 다진 마늘 2g(1/3작은술)', 45.00, 2.00, 1.00, 7.00, 2441.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00038_2.png', '국&찌개', '끓이기', NULL, NULL, '표고버섯 밑둥을 육수에 넣어 감칠맛을 더하고, 멸치와 다시마는 다량의 소금 없이도 국물 맛을 깊게 만들어 주는 역할을 해요. 건표고버섯은 햇볕에 말리는 과정에서 만들어진 비타민 D가 풍부해 칼슘 흡수를 도와 골다공증 예방에 좋은 식품이예요. 생표고버섯에는 비타민 D가 거의 들어있지 않아요.'),
(95, '치커리샐러드와 올리브 마늘 소스', '●치커리 샐러드 : 치커리 30g(10줄기), 적양배추 15g(5×3cm), 양파 10g(2×1cm), 당근 5g(3×1×1cm) ●올리브마늘 드레싱 : 올리브유 10g(2작은술), 식초 5g(1작은술), 설탕 5g(1작은술), 마늘 5g(1쪽)', 170.00, 3.00, 12.00, 13.00, 74.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00095_2.png', '반찬', '기타', NULL, NULL, '치커리를 육류와 함께 섭취하면 치커리의 산뜻한 맛으로 입맛을 돋우고 섬유소의 섭취를 늘릴 수 있으며 동맥경화를 예방하는데 도움이 된답니다. 치커리에는 칼륨이 많아 육류나 고나트륨 식사시 함께 섭취하면 체내나트륨 배출에 효과적이에요.'),
(87, '스트로베리 샐러드', '스트로베리 샐러드 딸기 70g(7개), 플레인요거트 85g(1개), 양상추 70g(2장), 메추리알 30g(3개), 블루베리 15g(1큰술), 식초 약간, 소금 약간', 195.00, 7.00, 5.00, 30.00, 138.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00087_2.png', '반찬', '기타', NULL, NULL, '딸기는 칼륨이 풍부하여 나트륨 배출에 뛰어나지만 칼슘이 부족한 과일이예요. 그렇기 때문에 칼슘이 풍부한 요거트나 기타 유제품과 함께 섭취하면 좋아요.'),
(89, '시금치 우유 소스와 그린매쉬드포테이토', '●그린매쉬드포테이토 : 감자 80g(1/2개), 시금치우유 소스 5g(1작은술), 아몬드 2g(1알), 설탕 2g(1/3작은술), 크랜베리 3g, 치커리 약간 ●시금치우유 소스 : 시금치 10g, 우유 10g(2작은술)', 155.00, 5.00, 1.00, 32.00, 24.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00089_2.png', '반찬', '찌기', NULL, NULL, '시금치에 풍부한 칼륨과 우유의 칼슘은 나트륨 배출을 도와줘요. 감자도 칼륨이 풍부한 채소이지만 감자에 부족한 칼슘을 유제품과 함께 섭취하면 칼슘을 보충 받을 수 있어요.'),
(91, '버섯구이와 두부타르타르 소스', '●버섯구이 : 새송이버섯 70g(7개), 올리브유 10g(2작은술) ●곁들임 : 치커리 10g(3줄기) ●두부타르타르 소스 : 연두부 30g(1/4모), 다진 양파 10g(2작은술), 다진 오이피클 10g(2작은술), 올리브유 2g(1/3작은술), 식초 5g(1작은술), 레몬즙 3g(2/3작은술), 머스터드 3g(2/3작은술), 꿀 2g(1/3작은술), 흰 후추 약간', 150.00, 8.00, 5.00, 18.00, 133.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00091_2.png', '반찬', '굽기', NULL, '연두부', '새송이 버섯은 칼륨 함량이 높아 육류와 함께 섭취하면 혈중 콜레스테롤 수치를 낮추는데 효과가 있어요. 나트륨이 많은 간장이나 소금 대신 겨자, 레몬 등을 이용한 소스로 나트륨 함량을 줄여보세요.'),
(93, '구운채소와 간장레몬 소스', '●구운채소 : 가지 20g(3cm), 호박 50g(1/3개), 새송이버섯 15g(3개), 양파 15g(1/8개), 발사믹크레마 15g(1큰술), 빨강 파프리카 3g(3×1cm), 노랑 파프리카 3g(3×1cm), 청피망 3g(3×1cm), 올리브유 약간 ●간장 레몬 소스 : 저염간장 5g(1작은술), 식초 15g(1큰술), 설탕 10g(1큰술), 레몬즙 5g(1작은술)', 175.00, 4.00, 9.00, 19.00, 38.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00093_2.png', '반찬', '굽기', NULL, NULL, '채소와 버섯류에는 대부분 나트륨 배출에 효과적인 칼륨이 함유되어 있어 채소를 많이 섭취하는 것이 저나트륨 식단의 기본이에요. 레몬즙은 음식의 짠맛을 감추고 간장과 함께 사용하면 간장의 감칠맛을 상승시키는 효 과도 있어요.'),
(94, '브로콜리 컬리플라워 샐러드와 두유 요거트 소스', '●브로콜리컬리플라워 샐러드 : 브로콜리 40g(1/5송이), 컬리플라워 40g(1/3송이), 적양파 10g(2×1cm), 강낭콩 5g(1작은술), 건포도 5알, 호두 1/2알, 소금 약간 ●두유요거트 소스 : 두유 50g(3⅓큰술), 플레인요거트 20g(1⅓큰술), 레몬즙 5g(1작은술), 설탕 약간', 210.00, 9.00, 6.00, 30.00, 79.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00094_2.png', '반찬', '기타', NULL, NULL, '브로콜리와 컬리플라워를 데치는 과정에서만 소금을 넣어 맛과 색은 살려주고 소금 사용은 줄였어요, 또한 마요네즈 대신 두유를 사용하면 맛은 고소해지고 나트륨 배설은 증가 시킬 수 있어요.'),
(108, '오렌지와 당근 만남주스', '오렌지 당근펀치 당근 100g(1/2개), 오렌지 100g(1/2개)', 60.00, 1.00, 0.00, 14.00, 76.00, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00108_1.png', '후식', '기타', '250', NULL, '시판되는 대부분의 주스는 가공되고 농축되는 과정에서 나트륨이 첨가되는 경우가 있어 과일, 채소를 직접 갈아 마시면 나트륨 섭취를 줄일 수 있어요.'),
(111, '칠곡석류국수', '●주재료 : 소면 160g, 저염소금 4g(1작은술), 식초 8g(1/2작은술) ●소스 : 석류 200g(1/2개) ●장식 : 잣 4g(1작은술), 아몬드 4g(1작은술), 해바라기씨 4g(1작은술), 호두 4g(1작은술), 호박씨 4g(1작은술), 오이 20g(1/4개)', 73.40, 2.10, 1.60, 12.70, 12.50, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00111_2.png', '일품', '끓이기', NULL, '저염소금', '견과류에는 불포화 지방산이 풍부하게 함유되어 있어, 고지혈증 등 혈류개선에 효과가 있어 성인병 예방에 좋다. 짠맛을 대신하여 석류의 새콤달콤한 맛을 활용하고, 볶은 견과류로 고소한 맛과 풍미를 더하였다.'),
(113, '블랙빈 곤약국수', '●주재료 실곤약 440g, 검은콩 70g, 볶은 흑임자 6g(1작은술), 호두 6g(1알) ●장식 오이 20g(1/8개), 토마토 50g(1/2개), 달걀 50g(1개)', 54.30, 3.40, 2.40, 4.90, 18.50, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00113_2.png', '일품', '끓이기', NULL, '실곤약', '검은콩은 안토시아닌 색소를 많이 함유하고 있어 시력회복과 항암작용에 좋다. 나트륨 함량이 높은 소면을 대신하여 실곤약으로 면을 대체함으로써 나트륨과 칼로리의 섭취량을 줄이고, 콩국물에 흑임자와 호두를 넣어 짠맛 대신 고소한 맛과 향을 강조한다.'),
(114, '닭가슴살 브로콜리 만두', '주재료 만두피 4g(2장), 브로콜리 100g(1/4개), 숙주 100g, 표고버섯 20g(2개), 닭 가슴살 100g, 참기름 2g(1/2작은술), 저염간장 4g(1작은술), 소금 0.5g, 후춧가루 0.1g', 123.20, 8.40, 1.50, 19.00, 167.90, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00114_2.png', '일품', '찌기', NULL, '닭가슴살', '칼륨 함량이 높은 브로콜리를 사용하여 나트륨 배출을 돕는다. 만두소는 저염간장으로 양념하고 별도로 초간장을 곁들이지 않아 나트륨섭취를 줄인다.'),
(115, '닭가슴살 샐러드와 발사믹', '닭가슴살 100g, 양상추 50g, 방울토마토 4개, 오이 1/3개, 발사믹 드레싱 20g', 180.00, 25.00, 5.00, 9.00, 150.00, 'http://www.example.com/img/recipe/115.png', '반찬', '기타', '300', '저염드레싱', '닭가슴살은 소금 대신 후추와 허브로 밑간하여 염분 섭취를 줄입니다. 채소를 다양하게 추가하여 비타민과 섬유질을 보충하세요.'),
(116, '저염 두부 조림', '두부 150g, 양파 1/4개, 대파 약간, 저염간장 10g(2작은술), 물 50ml, 다진 마늘 3g', 195.00, 15.00, 10.00, 9.00, 210.00, 'http://www.example.com/img/recipe/116.png', '반찬', '조림', '200', '저염간장', '조림 간은 저염간장으로 맞추고, 고춧가루나 고추냉이 등을 활용하여 매콤한 맛을 더하면 간이 약한 느낌을 줄일 수 있습니다.'),
(117, '단호박 수프', '단호박 200g, 우유 100ml, 양파 20g, 무염버터 5g, 꿀 5g', 230.00, 5.00, 8.00, 35.00, 50.00, 'http://www.example.com/img/recipe/117.png', '국&찌개', '끓이기', '350', '단호박', '소금 대신 우유나 생크림으로 농도를 맞추고, 단호박의 자연스러운 단맛을 최대한 살려 나트륨 첨가를 최소화합니다.'),
(118, '아보카도 명란 비빔밥', '밥 200g, 아보카도 1/2개, 저염 명란 30g, 달걀 1개, 김가루 약간, 참기름 5g', 450.00, 20.00, 25.00, 35.00, 380.00, 'http://www.example.com/img/recipe/118.png', '일품', '기타', '400', '저염명란', '명란젓의 염분이 높으므로, 반드시 저염 명란을 사용하고 소스 없이 참기름만으로 비벼 먹거나, 레몬즙을 첨가하면 좋습니다.'),
(119, '담백한 콩나물국', '콩나물 150g, 대파 10g, 국간장 5g(1작은술), 다진 마늘 2g, 물 400ml', 40.00, 4.00, 0.00, 6.00, 220.00, 'http://www.example.com/img/recipe/119.png', '국&찌개', '끓이기', '450', NULL, '콩나물 자체의 시원한 맛을 살리기 위해 소금 대신 국간장으로 최소한의 간만 하고, 멸치/다시마 육수를 활용하면 감칠맛이 살아납니다.');
INSERT INTO RecipeSteps (recipe_id, step_number, instruction, image_url) VALUES
(28, 1, '1. 손질된 새우를 끓는 물에 데쳐 건진다.a', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_1.png'),
(28, 2, '2. 연두부, 달걀, 생크림, 설탕에 녹인 무염버터를 믹서에 넣고 간 뒤 새우(1)를 함께 섞어 그릇에 담는다.b', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_2.png'),
(28, 3, '3. 시금치를 잘게 다져 혼합물 그릇(2)에 뿌리고 찜기에 넣고 중간 불에서 10분 정도 찐다.c', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_3.png'),
(29, 1, '1. 부추는 깨끗이 씻어 물기를 제거하고, 5cm 길이로 썰고 부추에 날콩가루를 넣고 고루 섞이도록 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054820_1675673300714.jpg'),
(29, 2, '2. 찜기에 면보를 깔고 부추를 넣은 후 김이 오르게 쪄서 파랗게 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054834_1675673314720.jpg'),
(29, 3, '3. 저염간장에 다진 대파, 다진 마늘, 고춧가루, 요리당 , 참기름, 참깨를 섞어 양념장을 만들고 찐 부추는 그릇에 담아낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054908_1675673348152.jpg'),
(31, 1, '1. 물기를 빼고 2cm 정도의 크기로 썰은 부추와 양파를 양념장에 섞어 양념속을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_1.png'),
(31, 2, '2. 깨끗이 씻은 방울토마토는 꼭지를 떼고 윗부분에 칼로 십자모양으로 칼집을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_4.png'),
(31, 3, '3. 칼집을 낸 방울토마토에 양념속을 사이사이에 넣어 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_5.png'),
(32, 1, '1. 사과, 순두부를 믹서에 넣고 곱게 갈아 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_1.png'),
(32, 2, '2. 오이는 소금으로 문질러 씻어 반을 갈라 씨를 제거하고 어슷썰기를 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_2.png'),
(32, 3, '3. 썰어 놓은 오이에 순두부사과 소스를 넣고 버무린 후 다진 땅콩을 뿌려 마무리 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_3.png'),
(33, 2, '2. 북어채를 잘게 손으로 찢어 찬물에 헹구어 준비한 후 양파, 표고버섯, 사과는 채 썰고 새우는 등쪽의 두세 마디에 꼬챙이를 넣어 내장을 뺀 후 헹군다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_2.png'),
(33, 3, '3. 찬물에 북어채, 새우, 표고버섯, 양파를 넣고 20분 정도 끓인 후 사과를 넣어 북어의 씁쓸한 맛을 없앤다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_3.png'),
(33, 5, '5. 그릇에 담아낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_5.png'),
(36, 3, '3. 황태는 손질하여 물에 헹궈 건져 놓고 콩나물은 다듬어 씻고 청양고추는 어슷썰기 한다.', NULL),
(36, 4, '4. 냄비에 물을 붓고 황태와 무를 넣고 끓인 후 육수에서 물을 건져내고 저염 된장을 푼다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_4.png'),
(36, 5, '5. 콩나물, 다진 마늘, 청양고추를 넣고 뚜껑을 덮어 김이 나게 끓 여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_5.png'),
(37, 1, '1. 감자, 양파는 얇게 썰고 애느타리 버섯은 썰어 달궈진 팬에 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_2.png'),
(37, 2, '2. 냄비에 물을 붓고 된장을 푼 뒤 감자, 양파, 두부를 넣어 재료가 투명해지게 끓인 후 된장국의 재료를 건져서 믹서에 넣어 갈고 된장국에 넣어 한번 더 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_4.png'),
(37, 3, '3. 구운 애느타리버섯과 대파를 국에 넣어 끓인 후 그릇에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_6.png'),
(38, 1, '1. 찬물에 표고버섯,머리와 내장을 제거한 멸치, 다시마, 양파를 넣어 중간 불에서 10분 정도 끓여 멸치육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_1.png'),
(38, 2, '2. 육수의 표고버섯 기둥, 멸치, 다시마, 양파를 건진 후 표고버섯은 편으로 썰고 청경채는 4cm 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_2.png'),
(38, 3, '3. 끓는 육수에 준비한 표고버섯과 청경채를 넣고 국간장으로 간을 한 후 마늘을 넣어 한소끔 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_4.png'),
(95, 1, '1. 올리브유, 식초, 설탕, 다진 마늘을 섞어 거품기로 충분히 저어주어 올리브마늘 드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_2.png'),
(95, 2, '2. 치커리는 싱싱하게 찬물에 담갔다가 물기를 뺀 후 한입 크기로 자르고, 적양배추, 양파, 당근은 곱게채를 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_3.png'),
(95, 3, '3. 접시에 준비한 치커리, 적양배추, 양파, 당근을 담고 올리브마늘 드레싱을 뿌린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_4.png'),
(87, 1, '1. 찬물이 담긴 냄비에 식초, 소금을 넣고 메추리알을 삶는다. 물이 끓어오르면 5분 정도 더 삶아 찬물에 헹군 후 껍질을 벗기고 반으로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_1.png'),
(87, 2, '2. 딸기를 흐르는 물에 가볍게 씻어 꼭지를 제거한 후 물기를 빼고 반으로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_2.png'),
(87, 3, '3. 양상추는 찬물에 담갔다가 물기를 빼고 한입 크기로 찢은 후 접시에 양상추, 딸기, 블루베리, 메추리알을 담고 플레인요거트를 끼얹는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_4.png'),
(89, 1, '1. 시금치는 끓는 소금물에 데쳐 찬물에 헹구어 물기를 짜고 우유를 넣고 블렌더에 곱게 갈아 체에 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_1.png'),
(89, 2, '2. 껍질 벗긴 감자는 찜기에 넣어 20분 정도 삶고 꺼내서 으깨고, 아몬드는 잘게 다지며, 치커리는 곱게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_2.png'),
(89, 3, '3. 으깬 감자, 다진 치커리, 시금치 우유소스, 설탕을 넣고 섞어 접시에 담고 아몬드, 크랜베리를 올리고 시금치우유 소스를 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_5.png'),
(91, 1, '1. 곱게 다진 양파와 오이피클은 물기를 짠다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_1.png'),
(91, 2, '2. 연두부에 다진 양파, 다진 오이피클, 올리브유, 머스터드, 후추, 꿀, 식초, 레몬즙을 넣어 두부타르타르 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_2.png'),
(91, 3, '3. 새송이버섯은 0.5cm 두께로 썰고 프라이팬에 노릇하게 구워준 후 접시에 새송이버섯과 치커리를 담고 소스를 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_5.png'),
(93, 1, '1. 저염간장, 식초, 레몬즙, 설탕을 혼합하여 간장레몬 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_1.png'),
(93, 2, '2. 호박, 가지, 새송이버섯은 3cm 길이로 자른 후 얇은 편으로 채 썰고, 양파, 파프리카, 피망은 호박 길이로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_2.png'),
(93, 3, '3. 가지, 호박, 새송이버섯, 양파, 파프리카, 피망에 올리브유를 바르고 달궈진 그릴 팬에 구운 후 접시에 담고 발사믹 크레타를 뿌리고 간장레몬 소스를 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_3.png'),
(94, 1, '1. 두유에 플레인요거트와 레몬즙, 설탕을 넣고 잘 섞어 두유요거트 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_1.png'),
(94, 2, '2. 강낭콩은 물에 불린 후 콩과 물을 1:2.5로 넣고 20~25분 삶아 건지고 브로콜리와 컬리플라워는 한입 크기로 썰어 끓는 소금물에 살짝 데쳐 찬물에 헹궈 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_4.png'),
(94, 3, '3. 접시에 데친 브로콜리와 컬리플라워, 삶은 강낭콩, 채썬 적양파, 다진 호두, 건포도를 담고 두유요거트 소스를 곁들여 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_5.png'),
(108, 1, '1. 오렌지는 깨끗이 씻어 껍질을 벗긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_1.jpg'),
(108, 2, '2. 당근은 깨끗이 씻어 작은 토막으로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_2.jpg'),
(108, 3, '3. 당근, 오렌지, 물(50ml)을 믹서에 곱게 간다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_3.jpg'),
(108, 4, '4. 믹서에 갈아낸 주스는 고운 체로 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_4.jpg'),
(108, 5, '5. 주스를 살얼음이 생길 만큼 시원하게 냉동한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_5.jpg'),
(108, 6, '6. 컵에 담아 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_6.jpg'),
(111, 1, '1. 잣, 아몬드, 해바라기씨, 호두, 호박씨는 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_1.png'),
(111, 2, '2. 끓는 물에 저염소금과 식초를 넣은 후 소면을 삶은 후 체에 건져 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_2.png'),
(111, 3, '3. 석류는 물 600g와 함께 믹서에 갈고 체에 걸러 즙을 낸다. 그릇에 면을 담고 차갑게 식힌 석류즙을 붓고 견과류, 채 썬 오이를 고명으로 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_3.png'),
(113, 1, '1. 검은콩을 1시간 찬물에 담가 불린 후 삶아 익힌 후 불순물을 제거하고 흑임자, 생수, 삶은 콩, 호두를 믹서에 갈아 살엄음이 생기도록 냉동실에 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_1.png'),
(113, 2, '2. 오이는 돌려 깎아 채 썰고, 토마토는 1/8크기로 썰고, 달걀은 삶아 반으로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_4.png'),
(113, 3, '3. 실곤약은 끓는 물에 데치고 찬물에 헹궈 그릇에 담은 후 콩물을 담고 오이, 토마토, 달걀을 고명으로 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_6.png'),
(114, 1, '1. 브로콜리, 숙주, 표고버섯, 닭 가슴살은 다지고 다진 닭 가슴살은 소금과 후추로 밑간 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_1.png'),
(114, 2, '2. 다진 채소와 닭가슴살, 참기름, 저염간장을 섞어 만두소를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_3.png'),
(114, 3, '3. 준비한 만두피에 만두소를 넣은 후 만두 모양으로 빚고 만두를 찜통에 넣어 찌고 그릇에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_4.png'),
(115, 1, '1. 닭가슴살은 끓는 물에 삶거나 찜기에 쪄서 잘게 찢어 준비합니다.', 'http://www.example.com/img/step/115_1.png'),
(115, 2, '2. 양상추, 방울토마토, 오이는 깨끗이 씻어 한입 크기로 썰어둡니다.', 'http://www.example.com/img/step/115_2.png'),
(115, 3, '3. 그릇에 채소와 닭가슴살을 담고 발사믹 드레싱을 뿌려 마무리합니다.', 'http://www.example.com/img/step/115_3.png'),
(116, 1, '1. 두부를 2cm 두께로 썰어 키친타월로 물기를 제거합니다.', 'http://www.example.com/img/step/116_1.png'),
(116, 2, '2. 저염간장, 물, 다진 마늘, 잘게 썬 양파를 섞어 조림 양념을 만듭니다.', 'http://www.example.com/img/step/116_2.png'),
(116, 3, '3. 팬에 두부를 올리고 양념을 부어 중약불에서 졸이고, 마지막에 대파를 올려 마무리합니다.', 'http://www.example.com/img/step/116_3.png'),
